sources.assets/scss/layout/_header.scss:
header {
    background-color: $background-elements;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-evenly;
    @include tablet{
      justify-content: space-between;
    }
}

.presentation {
    color: $title;
    font-family: 'JetBrains Mono', monospace;
    padding-top: 0.5em;
    padding-left: 2em;
    h1 {
        font-size: 2em;
        padding-bottom: 0.5em;
    }
    @include tablet {
      font-size: 0.7em;
      h1 {
          font-size: 1.2em;
      }
    }
    @include tabland {
      font-size: 1em;
      h1 {
          font-size: 2em;
      }
    }
}

.area {
    display: flex;
    padding-top: 0.5em;
    padding-left: 0.5em;
    color: $title;
    font-family: 'JetBrains Mono', monospace;
    img {
        width: 3em;
        height: 3em;
    }
    @include tablet {
        font-size: 0.7em;
        padding-left: 0;
    }
    @include tabland {
        font-size: 1em;
    }
}

.header--links {
    display: flex;
    margin-top: 1em;
    padding-right: 2em;
    img {
        width: 2em;
        height: 2em;
        padding-left: 1em;
    }
    .github {
        img:hover {
        content: url(../../assets/images/Github-logo-hover.png)
        }
    }
    .linkedin {
        img:hover {
        content: url(../../assets/images/linked-logo2-hover.png)
        }
    }
    .arobase {
        img:hover {
        content: url(../../assets/images/arobase-hover.png)
        }
    }

    @include tablet {
      margin-top: 0;
      padding-top: 0.5em;
      font-size: 0.7em;
      img {
        width: 2.5em;
        height: 2.5em;
        padding-left: 0.5em;
      }
    }
    @include tabland {
      font-size: 1em;
      img {
        width: 3em;
        height: 3em;
      }
    }
}

sources.assets/scss/layout/_global.scss:
@font-face {
  font-family: 'pt_sansregular';
  src: url('../fonts/PTS55F-webfont.eot');
  src: url('../fonts/PTS55F-webfont.eot?#iefix') format('embedded-opentype'),
        url('../fonts/PTS55F-webfont.woff') format('woff'),
        url('../fonts/PTS55F-webfont.ttf') format('truetype'),
        url('../fonts/PTS55F-webfont.svg#pt_sansregular') format('svg');
  font-weight: normal;
  font-style: normal;
}

body {
  background-color: $background;
  font-size: 16px;
  font-family: 'pt_sansregular', Arial, serif;
  height: 100vh;
  display: flex;
  flex-direction: column;
}

main {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-evenly;

  @include tabland {
    justify-content: space-between;
  }
}

.picture {
  width: 10em;
  height: 11em;
  border: 2px solid $background-elements;
  margin: 2em 1em 0 2em;
  background-image: url(../../assets/images/XD_mini.jpg);
  background-size: contain;
  background-repeat: no-repeat;
  a {
    display: none;
  }

  @include tabland {
    width: 7em;
    height: 7.7em;
    border: 2px solid $background-elements;
    margin: 2em 0;
    background-image: url(../../assets/images/XD_mini.jpg);
    background-size: contain;
    background-repeat: no-repeat;
    a {
      display: none;
    }
  }
  @include laptop {
    width: 10em;
    height: 11em;
    border: 2px solid $background-elements;
    margin: 2em 1em 2em 2em;
    background-image: none;
    a {
      display: block;
      text-decoration: none;        
    }
    a:hover {
      content: url(../../assets/images/XD_mini.jpg);
      width: 100%;
      height: 100%;
    }
  }
  &--before {
    display: none;

    @include laptop {
      display: block;
      img {
        filter: grayscale(1);
        width: 70%;
        height: 70%;
        display: block;
        margin: 0.5em auto;
        }
      p {
        color: $background-elements;
        text-align: center;
        }
    }
  }
}

.intro {
  margin-top: 2em;
  width: 16.5em;
  color: $title;
  font-family: 'JetBrains Mono', monospace;
  font-style: italic;
  line-height: 1.2em;

  @include tablet {
    font-size: 0.9em;
    margin-right: 1em;
    padding-top: 1em;
  }
  @include tabland {
    font-size: 0.7em;
  }
  @include laptop {
    font-size: 1em;
    margin-right: 0;
  }
}

.education {
  margin-top: 2em;
  padding: 1em;
  color: $title;
  font-family: 'JetBrains Mono', monospace;
  font-size: 0.8em;
  line-height: 1.5em;
  h2
  {
    font-size: 1.5em;
  }
  a {
    color: $title;
    font-weight: bold;
  }  

  @include tabland {
    order: -1;
    font-size: 0.7em;
  }
  @include laptop {
    order: initial;
    font-size: 1em;
  }
}

.skills {
  margin: 4em 1em 0;
  padding-top: 1em;
  height: 3em;
  border: 3px solid $title;
  border-radius: 10px;
  -webkit-transform: rotate(-3deg);
   -moz-transform: rotate(-3deg);
    -ms-transform: rotate(-3deg);
     -o-transform: rotate(-3deg);
        transform: rotate(-3deg);
  a {
    text-align: center;
    text-decoration: none;
    text-transform: uppercase;
    color: $title;
    font-weight: bold;
    padding: 2em;
  }
  a:hover {
  color: $background;
  }

  @include tabland {
    display: none;
  }
  @include laptop {
    display: block;
    margin: 3em 2em 5em 25em;
  }
  @include desktop {
    margin: 4em 1em 0;
  }
}
.skills:hover {
  background-color: $title;
}

.responsive {
  width: 17em;
  height: 12em;
  margin: 2em;
  img {
    width: 100%;
    height: 100%
  }
  p {
    color: $title;
    margin-left: 6em;
    margin-top: -1em;
  }

  @include tabland {
    display: none;
  }
  @include laptop {
    display: block;
    margin: 2em 20em 2em 2em;
  }
  @include desktop {
    margin: 2em;
  }
}

sources.assets/scss/layout/_footer.scss:
footer {
    display: flex;
    margin-top: auto;
}

// partie "effet escalier" visible  partir du breakpoint 'tabland'
.effect {
  display: none;

  @include tabland {
    display: block;
    height: 20em;
    background: linear-gradient($background,$background-elements);
    min-width: 35%;
    color: $title;
    flex-direction: column;
    .effect--top
    {
      padding: 0.5em 1em;
      text-align: center;
    }
  }
  @include laptop {
    height: 30em;
  }
}

.tabMob {
  @include tabland {
    display: block;
    &--skills{
      margin: 1em auto;
      padding: 0.5em;
      width: 5em;
      height: 2em;
      border: 3px solid $title;
      border-radius: 10px;
      -webkit-transform: rotate(-3deg);
      -moz-transform: rotate(-3deg);
        -ms-transform: rotate(-3deg);
        -o-transform: rotate(-3deg);
            transform: rotate(-3deg);
      text-align: center;
      a {
        text-align: center;
        text-decoration: none;
        text-transform: uppercase;
        color: $title;
        font-weight: bold;
      }
    }
    &--responsive {
      width: 12em;
      height: 9em;
      margin: 0.5em auto;
      img {
        width: 100%;
        height: 100%
      }
      p {
        color: $title;
        margin-left: 4em;
        margin-top: -1em;
      }
    }
  }
  @include laptop {
    display: none;
  }
}

.stair {
  @include tabland {
    display: none;
  }
  @include laptop {
    display: block;
  }
}

.Words {
  width: 60%;
  font-family: Helvetica, Arial, sans-serif;
  font-size: 2.2em;
  font-weight: 900;
  letter-spacing: -2px;
  text-transform: uppercase;
  -webkit-transform: translate3d(0, 0, 0);
          transform: translate3d(0, 0, 0);
  -webkit-font-smoothing: antialiased;
  -webkit-font-kerning: normal;
  -webkit-text-size-adjust: 100%;

  @include desktop {
    font-size: 3em;
  }
}

.Words-line {
  height: 35px;
  overflow: hidden;
  position: relative;

  @include desktop {
    height: 40px;
  }
}

.Words-line:nth-child(odd) {
  -webkit-transform: skew(60deg, -30deg) scaleY(0.66667);
          transform: skew(60deg, -30deg) scaleY(0.66667);
}

.Words-line:nth-child(even) {
  -webkit-transform: skew(0deg, -30deg) scaleY(1.33333);
          transform: skew(0deg, -30deg) scaleY(1.33333);
}

.Words-line:nth-child(1) {
  left: 29px;
}

.Words-line:nth-child(2) {
  left: 58px;
}

.Words-line:nth-child(3) {
  left: 87px;
}

.Words-line:nth-child(4) {
  left: 116px;
}

.Words-line:nth-child(5) {
  left: 145px;
}

.Words-line:nth-child(6) {
  left: 174px;
}

.Words-line:nth-child(7) {
  left: 203px;
}

.Words-line p {
  height: 50px;
  line-height: 45px;
  -webkit-transition: all .4s ease-in-out;
  transition: all .4s ease-in-out;
  -webkit-transform: translate3d(0, 0, 0);
          transform: translate3d(0, 0, 0);
  vertical-align: top;
  white-space: nowrap;
}

.colorP {
  color: $background;
  font-style: italic;
}

.Words:hover p {
  -webkit-transform: translate3d(0, -50px, 0);
          transform: translate3d(0, -50px, 0);
}

.Words:hover p {
  -webkit-transform: translate3d(0, -50px, 0);
          transform: translate3d(0, -50px, 0);
}

// barre de prsentation des projets
.info {
  position: relative;
  background-color: $background-elements;
  min-width: 8%;
  height: 20em;
  p {
    writing-mode: vertical-rl;
    text-orientation: upright;
    font-size: 2em;
    font-weight: bold;
    text-transform: uppercase;
    margin-left: auto;
    padding-top: 1em;
    padding-right: 0.3em;
    color: $background-elements;
    -webkit-text-stroke: 1px;
    -webkit-text-stroke-color: $title;
  }

  @include tabland {
    min-width: 4%;
  }
  @include laptop {
    height: 30em;
  }

  &--tab {
    display: none;

    @include laptop {
      display: block;
      position: absolute;
      top: -2em;
      height:2.5em;
      width: 100%;
      background-color: $background-elements;
      border-top-right-radius : 10px;
      border-top-left-radius : 10px;
    }
  }
  &--tab2 {
    position: absolute;
    margin-top: 2em;
    right: -2.15em;
    height:3em;
    width: 2.2em;
    background-color: $background-elements;
    border-top-right-radius : 10px;
    border-bottom-right-radius : 10px;

    @include tablet {
      right: -2em;
    }
  }
}

//visuels des projets raliss
.projects {
  img {
    width: 90%;
    height: 15em;
    display: block;
    margin: auto;

    @include tablet {
      height: 18em;
    }
    @include laptop {
      height: 25em;
      margin: 2em auto;
    }
  }
  p {
    font-size: 2em;
    color: $title;
    text-align: center;
    margin-top: 0.7em;

    @include tablet {
      display: none;
    }
  }
}
